INSERT INTO `tbl_content` VALUES ('71', '9', '0', 'CN hàn mới cho tàu vận chuyển khí tự nhiên ở Bắc cực', 'cn-han-moi-cho-tau-van-chuyen-khi-tu-nhien-o-bac-cuc', 'good-pupil-won-a-scholarship-to-study-in-singapore', 'CN hàn mới cho tàu vận chuyển khí tự nhiên ở Bắc cực', null, 'Đây là một phần của chiến lược lâu dài của công ty đối với sự gia tăng nhu cầu về vận chuyển LNG và LNG FPSOs được sử dụng trong các vùng cực.&nbsp;Điều này&nbsp;cũng&nbsp;thể hiện&nbsp;sự&nbsp;quan tâm ngày càng tăng&nbsp;đến&nbsp;việc khai thác&nbsp;khí thiên nhiên&nbsp;ở những vùng&nbsp;cực.&nbsp;HHI hy vọng sẽ làm&nbsp;một cuộc&nbsp;thâm nhập vào&nbsp;các hãng&nbsp;vận chuyển LNG&nbsp;ở các vùng&nbsp;cực và thị trường LNG FPSO&nbsp;với các&nbsp;dự án phát triển khí đốt ở Bắc Băng Dương, nơi được ước tính có 44 tỷ thùng khí tự nhiên, hoặc 30% trữ lượng khí thiên nhiên thế giới.', '<div class=\"Block newsDetail_Content Clear\" style=\"margin:0px;padding:0px 5px;clear:both;font-size:12px;font-family:Arial, Helvetica, sans-serif;color:#736551;text-align:justify;line-height:1.5;background-color:#ffffff;overflow:hidden\"><p style=\"margin:0in 0in 0pt;padding:0px;line-height:normal;vertical-align:top\"><span style=\"margin:0px;padding:0px;font-size:14px;font-family:\'times new roman\', times, serif\" class=\"&lt;img src=\" http:=\"\" hakuta.com.vn=\"\" backend=\"\" assets=\"\" 9c8ce108=\"\" images=\"\" pixel.gif\"=\"\" title=\"http://static.bizwebmedia.net/images/print.png\" rel=\"media9591126358136535\" width=\"100\" height=\"100\">\"&gt;<span style=\"margin:0px;padding:0px;color:#000000\">Hyundai Heavy Industries (HHI) của Hàn Quốc gần đây đã thông báo kế hoạch&nbsp;phát triển một công nghệ hàn đặc biệt cho&nbsp;</span><span style=\"margin:0px;padding:0px;color:#000000\">bồn</span><span style=\"margin:0px;padding:0px;color:#000000\">&nbsp;vận chuyể</span><span style=\"margin:0px;padding:0px;color:#000000\">n khí tự nhiên hóa lỏng</span><span style=\"margin:0px;padding:0px;color:#000000\">&nbsp;LNG</span><span style=\"margin:0px;padding:0px;color:#000000\">&nbsp;tại các vùng&nbsp;ở&nbsp;Bắc cực</span><span style=\"margin:0px;padding:0px;color:#000000\">.<br style=\"margin:0px;padding:0px\" /></span><span style=\"margin:0px;padding:0px;background-color:#e6ecf9;color:#000000;background-position:initial initial;background-repeat:initial initial\"><br style=\"margin:0px;padding:0px\" /></span><span style=\"margin:0px;padding:0px;color:#000000\">Mục tiêu của dự án này là&nbsp;</span><span style=\"margin:0px;padding:0px;color:#000000\">chế tạo</span><span style=\"margin:0px;padding:0px;color:#000000\">&nbsp;một hệ thống thiết bị hàn đặc biệt có thể&nbsp;</span><span style=\"margin:0px;padding:0px;color:#000000\">hàn</span><span style=\"margin:0px;padding:0px;color:#000000\">&nbsp;các tấm nhôm dày được sử dụng cho&nbsp;</span><span style=\"margin:0px;padding:0px;color:#000000\">bồn chứa&nbsp;</span><span style=\"margin:0px;padding:0px;color:#000000\">&nbsp;LNG.&nbsp;</span><span style=\"margin:0px;padding:0px;color:#000000\">K</span><span style=\"margin:0px;padding:0px;color:#000000\">ỹ năng hàn</span><span style=\"margin:0px;padding:0px;color:#000000\">&nbsp;phát triển cao</span><span style=\"margin:0px;padding:0px;color:#000000\">&nbsp;là cần thiết để xây dựng b</span><span style=\"margin:0px;padding:0px;color:#000000\">ồn</span><span style=\"margin:0px;padding:0px;color:#000000\">chứa LNG khi nhu cầu về an toàn ở các vùng cực lạnh đòi hỏi độ dày của tấm</span><span style=\"margin:0px;padding:0px;color:#000000\">&nbsp;là</span><span style=\"margin:0px;padding:0px;color:#000000\">&nbsp;50mm đến 70mm.<br style=\"margin:0px;padding:0px\" />Bằng cách phát triển thiết bị hàn</span><span style=\"margin:0px;padding:0px;color:#000000\">&nbsp;và</span><span style=\"margin:0px;padding:0px;color:#000000\">&nbsp;xe hàn</span><span style=\"margin:0px;padding:0px;color:#000000\">&nbsp;với thiết kế mới&nbsp;</span><span style=\"margin:0px;padding:0px;color:#000000\">, HHI&nbsp;</span><span style=\"margin:0px;padding:0px;color:#000000\">dự kiến</span><span style=\"margin:0px;padding:0px;color:#000000\">&nbsp;tăng số lượng kim loại hàn từ 35g đến 60g mỗi phút và hy vọng sẽ cải thiện tốc độ và năng lực hàn&nbsp;</span><span style=\"margin:0px;padding:0px;color:#000000\">lên</span><span style=\"margin:0px;padding:0px;color:#000000\">&nbsp;hơn 30%.<br style=\"margin:0px;padding:0px\" />Sau khi&nbsp;</span><span style=\"margin:0px;padding:0px;color:#000000\">con tàu vận chuyển</span><span style=\"margin:0px;padding:0px;color:#000000\">&nbsp;LNG đầu tiên được đóng vào năm 1994, HHI hiện đã&nbsp;có&nbsp;đội tàu</span><span style=\"margin:0px;padding:0px;color:#000000\">&nbsp;với</span><span style=\"margin:0px;padding:0px;color:#000000\">tổng số 37 tàu&nbsp;</span><span style=\"margin:0px;padding:0px;color:#000000\">chở&nbsp;</span><span style=\"margin:0px;padding:0px;color:#000000\">LNG.</span></span></p><p style=\"margin:0in 0in 0pt;padding:0px;line-height:normal;vertical-align:top\"><span style=\"margin:0px;padding:0px;font-size:14px;font-family:\'times new roman\', times, serif\" class=\"&lt;img src=\" http:=\"\" hakuta.com.vn=\"\" backend=\"\" assets=\"\" 9c8ce108=\"\" images=\"\" pixel.gif\"=\"\" title=\"http://static.bizwebmedia.net/images/print.png\" rel=\"media15723894140683115\" width=\"100\" height=\"100\">\"&gt;<em style=\"margin:0px;padding:0px;color:#000000\" class=\"undefined\">Theo Motorship</em></span></p></div><br /><div><div><a><img alt=\"Bookmark and Sharing\" src=\"http://media.bizwebmedia.net/images/share-en.gif\" /></a></div><div><div><iframe frameborder=\"0\" hspace=\"0\" marginheight=\"0\" marginwidth=\"0\" scrolling=\"no\" tabindex=\"0\" vspace=\"0\" width=\"100%\" src=\"https://apis.google.com/_/+1/fastbutton?bsv&amp;size=small&amp;hl=en-US&amp;origin=http%3A%2F%2Fwww.ngoclinh.net.vn&amp;url=http%3A%2F%2Fwww.ngoclinh.net.vn%2Fcn-han-moi-cho-tau-van-chuyen-khi-tu-nhien-o-bac-cuc%2Fa29542.html&amp;gsrc=3p&amp;ic=1&amp;jsh=m%3B%2F_%2Fscs%2Fapps-static%2F_%2Fjs%2Fk%3Doz.gapi.en.cHT7VItG4Mw.O%2Fm%3D__features__%2Fam%3DEQ%2Frt%3Dj%2Fd%3D1%2Frs%3DAItRSTPKnxHeL9eLN3s5c4t29oGpxgOaRA#_methods=onPlusOne%2C_ready%2C_close%2C_open%2C_resizeMe%2C_renderstart%2Concircled&amp;id=I0_1370859234734&amp;parent=http%3A%2F%2Fwww.ngoclinh.net.vn&amp;rpctoken=80036945\" allowtransparency=\"true\" gapiattached=\"true\" title=\"+1\"></iframe></div></div><a href=\"http://www.ngoclinh.net.vn/cn-han-moi-cho-tau-van-chuyen-khi-tu-nhien-o-bac-cuc/a29542.html#\" title=\"In văn bản\"><img src=\"http://static.bizwebmedia.net/images/print.png\" title=\"In văn bản\" alt=\"In văn bản\" /></a></div>  <div><br /><br /></div>', 'post', '261', '1', null, 'a:5:{s:7:\"220_165\";a:2:{s:3:\"url\";s:52:\"/media/news/220_165/e2c420d9471370859168_220_165.jpg\";s:3:\"ext\";s:3:\"jpg\";}s:3:\"add\";a:2:{s:3:\"url\";s:44:\"/media/news/add/e2c420d9471370859248_add.jpg\";s:3:\"ext\";s:3:\"jpg\";}s:3:\"top\";a:2:{s:3:\"url\";s:44:\"/media/news/top/e2c420d9471370859253_top.jpg\";s:3:\"ext\";s:3:\"jpg\";}s:4:\"full\";a:2:{s:3:\"url\";s:46:\"/media/news/full/e2c420d9471370859260_full.jpg\";s:3:\"ext\";s:3:\"jpg\";}s:5:\"thumb\";a:2:{s:3:\"url\";s:48:\"/media/news/thumb/e2c420d9471370859266_thumb.jpg\";s:3:\"ext\";s:3:\"jpg\";}}', 'vi', '0', '2012-12-04 23:10:13', '1', '2013-06-10 17:14:28', '1', '0', '5', null, null, '1');
